import React, { useState } from "react";

import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Image,
  TouchableNativeFeedback,
  Button,
  Alert,
  Dimensions,
  TextInput,
  TouchableOpacity,
  ImageBackground,
} from "react-native";

export default function MainScreen({
  navigation,
  setResult,
  setFields,
  setTime,
}) {
  const [database, setDatabase] = useState("");
  const [query, setquery] = useState("");
  //const [result, setResult] = useState([]);

  const fetch_results = () => {
    return fetch("http://18.212.8.219:5001/api/fetch_results", {
      method: "POST",
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify({ query: query, database: database }),
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        if (json.status === "Done") {
          console.log("Connected to Amazon RDS", json.result);
          setResult(json.result);
          setFields(json.fields);
          setTime(json.time);
          navigation.navigate("ResultScreen");
          //setUserID(user)
          //history.push('/auth/main');
        }
      })
      .catch((error) => {
        console.error(error);
      });
    //console.log("Fetch records working");
  };
  console.log(query);
  console.log(database);
  return (
    <View style={styles.container}>
      <View style={styles.textInputView}>
        <TextInput
          placeholder={"Tap to enter SQL query"}
          keyboardType={"default"}
          width={"100%"}
          style={styles.textInput}
          multiline={true}
          onChangeText={setquery}
        />
      </View>
      <View style={styles.title}>
        <Text style={styles.titleText}>Select a Database</Text>
      </View>
      <View style={styles.dbButtonView}>
        <View style={styles.dbButton}>
          <TouchableOpacity>
            <Text
              style={styles.dbButtonText}
              onPress={() => setDatabase("RDS")}
            >
              Amazon RDS
            </Text>
          </TouchableOpacity>
        </View>
        <View style={styles.dbButton}>
          <TouchableOpacity>
            <Text
              style={styles.dbButtonText}
              onPress={() => setDatabase("Redshift")}
            >
              Amazon Redshift
            </Text>
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.button}>
        <TouchableOpacity onPress={fetch_results}>
          <Text style={styles.buttonText}>Fetch Records</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
